import React from "react"

export default function Game(){
    
    let [diceData, setDiceData] = React.useState([
        {
            id: 1,
            value: 6
        },
        {
            id: 2,
            value: 6
        },
        {
            id: 3,
            value: 6
        },
        {
            id: 4,
            value: 6
        },
        {
            id: 5,
            value: 6
        },
        {
            id: 6,
            value: 6
        },
        {
            id: 7,
            value: 6
        },
        {
            id: 8,
            value: 6
        },
        {
            id: 9,
            value: 6
        },
        {
            id: 10,
            value: 6
        }
    ]);
    
    function generateRandom(){
        console.log("button clicked")
        setDiceData((oldData) =>
            oldData.map((data) => ({
              ...data, // Create a new object to ensure immutability
              value: Math.floor(Math.random() * 6) + 1,
            }))
        );
    }

    let dices = diceData.map((dice) => {
        return <button className = "dice-button" key={dice.id}>{dice.value}</button>
    })

    return(
        <div>
            <div id="stats"></div>
            <div id="dice-container">
                {dices}
            </div>
            <div id="roll-button-container">
                <button onClick={generateRandom}>Roll dice</button>
            </div>
        </div>
    )
}
